import React, { useState, useRef, useCallback, MouseEvent } from 'react';
import './App.css';

type Props = MasterProps & LinkStateToProps & LinkDispatchToProps;

const App = (Props: Props) => {
  const [containerWidth, setContainerWidth] = useState(640);
  const [isBeingStretched, setIsBeingStretched] = useState(false);
  const masterRef = useRef<HTMLDivElement>(null);

  const handleMouseDown = () => {
    setIsBeingStretched(true);
  };

  const handleMouseMove = useCallback((event: MouseEvent) => {
    if (!isBeingStretched || !masterRef.current) return;
    const newWidth = event.clientX - masterRef.current.getBoundingClientRect().left;
    setContainerWidth(newWidth);
  }, [isBeingStretched]);

  const handleMouseUp = () => {
    setIsBeingStretched(false);
  };

  React.useEffect(() => {
    if (isBeingStretched) {
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
    } else {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    }

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, [isBeingStretched, handleMouseMove, handleMouseUp]);

  return (
    <div className="master-wrapper" ref={masterRef}>
      <div className="stretchable-div" style={{ width: `${containerWidth}px` }}>
        <div className="dragger-wrapper">
          <h2>This is supposed to change width</h2>
          <div
            className="dragger"
            id="dragger"
            onMouseDown={handleMouseDown}
          />
        </div>
      </div>
    </div>
  );
};

export default App;
