import React, { useState } from 'react';
import { Resizable } from 'react-resizable';
import './App.css'; // Assuming you have some CSS for styling

const ResizableComponent = ({ id, defaultWidth, defaultHeight, children }) => {
  const [width, setWidth] = useState(defaultWidth);
  const [height, setHeight] = useState(defaultHeight);

  const onResize = (event, { size }) => {
    setWidth(size.width);
    setHeight(size.height);
  };

  return (
    <Resizable
      className="resizable"
      width={width}
      height={height}
      onResize={onResize}
      draggableOpts={{ grid: [1, 1] }} // Optional, for smoother resizing
    >
      <div className="component" id={id}>
        {children}
      </div>
    </Resizable>
  );
};

const App = () => {
  return (
    <div className="app">
      <div className="wrapper">
        <ResizableComponent id="component1" defaultWidth={200} defaultHeight={200}>
        <div className="component">
            <h2>Component 1</h2>
            <p>This is some content for Component 1.</p>
            <img src="https://media.gettyimages.com/id/1335295270/photo/global-connection.jpg?s=612x612&w=gi&k=20&c=xs_DvcvEflA-EIRXXGK71Et6OtVHldTx2E7flyjybk0=" alt="Image 1" />
          </div>
        </ResizableComponent>
        <ResizableComponent id="component2" defaultWidth={200} defaultHeight={200}>
            <h2>Component 1</h2>
            <p>This is some content for Component 1.</p>
            <img
              src="https://media.gettyimages.com/id/1335295270/photo/global-connection.jpg?s=612x612&w=gi&k=20&c=xs_DvcvEflA-EIRXXGK71Et6OtVHldTx2E7flyjybk0="
              alt="Image 1"
            />

        </ResizableComponent>
        <ResizableComponent id="component3" defaultWidth={200} defaultHeight={200}>
        <div className="component">
            <h2>Component 1</h2>
            <p>This is some content for Component 1.</p>
            <img
              src="https://media.gettyimages.com/id/1335295270/photo/global-connection.jpg?s=612x612&w=gi&k=20&c=xs_DvcvEflA-EIRXXGK71Et6OtVHldTx2E7flyjybk0="
              alt="Image 1"
            />
          </div>
        </ResizableComponent>
      </div>
    </div>
  );
};

export default App;
