import React, { useState } from 'react';
import './App.css';

const App = () => {
  const [dimensions, setDimensions] = useState({ width: 300, height: 500 }); // Initial dimensions of the component

  const handleResize = (newDimensions) => {
    setDimensions(newDimensions);
  };

  return (
    <>
      <div
        className="resizable-component"
        style={{ width: `${dimensions.width}px`, height: `${dimensions.height}px` }}
      >
        <div className="content">
          {/* Your content here */}
        </div>
        <div
          className="resize-handle"
          onMouseDown={(e) => {
            const initialDimensions = dimensions;
            const initialX = e.clientX;
            const initialY = e.clientY;

            const handleMouseMove = (event) => {
              const deltaX = event.clientX - initialX;
              const deltaY = event.clientY - initialY;
              let newWidth = initialDimensions.width;
              let newHeight = initialDimensions.height;

              // Adjust dimensions based on which side is being resized
              if (e.target.className.includes('right')) {
                newWidth = initialDimensions.width + deltaX;
              }
              if (e.target.className.includes('bottom')) {
                newHeight = initialDimensions.height + deltaY;
              }
              if (e.target.className.includes('left')) {
                newWidth = initialDimensions.width - deltaX;
              }
              if (e.target.className.includes('top')) {
                newHeight = initialDimensions.height - deltaY;
              }

              handleResize({ width: newWidth, height: newHeight });
            };

            const handleMouseUp = () => {
              window.removeEventListener('mousemove', handleMouseMove);
              window.removeEventListener('mouseup', handleMouseUp);
            };

            window.addEventListener('mousemove', handleMouseMove);
            window.addEventListener('mouseup', handleMouseUp);
          }}
        />
        <div className="resize-handle top" />
        <div className="resize-handle right" />
        <div className="resize-handle bottom" />
        <div className="resize-handle left" />
      </div>
    </>
  );
};

export default App;
