import React, { useState } from 'react';
import './App.css';

const App = () => {
  const [width, setWidth] = useState(200); // Initial width of the component

  const handleResize = (newWidth) => {
    setWidth(newWidth);
  };

  const handleShrink = () => {
    if (width > 100) { // Minimum width condition
      setWidth(width - 50); // Decrease width by 50px
    }
  };

  const handleExpand = () => {
    setWidth(width + 50); // Increase width by 50px
  };
  return ( 
    <>
      <div className="resizable-component" style={{ width: `${width}px` }}>
      <div className="content">
        {/* Your content here */}
      </div>
      <div className="controls">
        <button onClick={handleShrink}>Shrink</button>
        <button onClick={handleExpand}>Expand</button>
      </div>
      <div
        className="resize-handle"
        onMouseDown={(e) => {
          const initialWidth = width;
          const initialX = e.clientX;

          const handleMouseMove = (event) => {
            const newWidth = initialWidth + (event.clientX - initialX);
            handleResize(newWidth);
          };

          const handleMouseUp = () => {
            window.removeEventListener('mousemove', handleMouseMove);
            window.removeEventListener('mouseup', handleMouseUp);
          };

          window.addEventListener('mousemove', handleMouseMove);
          window.addEventListener('mouseup', handleMouseUp);
        }}
      />
    </div>

    </>
   );
}

export default App;
