import React from "react";
import FileTree from "./FileTree";
import Editor from "./Editor";
import Terminal from "./Terminal";
import SampleSplitter from "./SampleSplitter";
import { useResizable } from "react-resizable-layout";

const Box = () => {
  const {
    isDragging: isFileDragging,
    position: fileW,
    splitterProps: fileDragBarProps
  } = useResizable({
    axis: "x",
    initial: 250,
    min: 50
  });

  const {
    isDragging: isTerminalDragging,
    position: terminalH,
    splitterProps: terminalDragBarProps
  } = useResizable({
    axis: "y",
    initial: 150,
    min: 50,
    reverse: true
  });

  return (
    <>
      <div className={"flex flex-column h-screen bg-dark font-mono color-white overflow-hidden"}>
        <div className={"flex grow"}>
          <FileTree isDragging={isFileDragging} width={fileW} />
          <SampleSplitter isDragging={isFileDragging} {...fileDragBarProps} />
          <div style={{ width: 10 }} />
          <Editor />
        </div>
        {/* ================================================== */}
        <SampleSplitter dir={"horizontal"} isDragging={isTerminalDragging} {...terminalDragBarProps} style={{marginLeft:"10px"}}/>
        <Terminal isDragging={isTerminalDragging} height={terminalH} />
      </div>
    </>
  );
};

export default Box;
