import React, { useState } from 'react';
import './App.css';

const App = () => {
  const [dimensions, setDimensions] = useState({ width: 300, height: 200 });
  const [isResizing, setIsResizing] = useState(false);
  const [resizeDirection, setResizeDirection] = useState('');

  const handleResizeStart = (direction, e) => {
    e.preventDefault();
    setIsResizing(true);
    setResizeDirection(direction);
  };

  const handleResizeEnd = () => {
    setIsResizing(false);
  };

  const handleMouseMove = (e) => {
    if (!isResizing) return;

    const { clientX, clientY } = e;
    let newWidth = dimensions.width;
    let newHeight = dimensions.height;

    switch (resizeDirection) {
      case 'right':
        newWidth = clientX - e.target.getBoundingClientRect().left;
        break;
      case 'bottom':
        newHeight = clientY - e.target.getBoundingClientRect().top;
        break;
      case 'left':
        const deltaLeft = e.target.getBoundingClientRect().left - clientX;
        newWidth = dimensions.width + deltaLeft;
        break;
      case 'top':
        const deltaTop = e.target.getBoundingClientRect().top - clientY;
        newHeight = dimensions.height + deltaTop;
        break;
      default:
        break;
    }

    setDimensions({ width: newWidth, height: newHeight });
  };

  return (
    <div className="resizable-container">
      <div
        className="resizable-component"
        style={{ width: dimensions.width, height: dimensions.height }}
        onMouseMove={handleMouseMove}
        onMouseUp={handleResizeEnd}
      >
        <div className="resize-handle top-left" onMouseDown={(e) => handleResizeStart('topLeft', e)} />
        <div className="resize-handle top-right" onMouseDown={(e) => handleResizeStart('topRight', e)} />
        <div className="resize-handle bottom-left" onMouseDown={(e) => handleResizeStart('bottomLeft', e)} />
        <div className="resize-handle bottom-right" onMouseDown={(e) => handleResizeStart('bottomRight', e)} />
        {/* Your content here */}
      </div>
    </div>
  );
};

export default App;
